  // å‰²å¼•ç‡ã®æŠ½å‡ºï¼ˆ%ï¼‰
  const rateMatch = text.match(/(\d+)%|é‚„å…ƒç‡[:ï¼š]?\s*(\d+)/);
  if (rateMatch) {
