  // é‡‘é¡ã®æŠ½å‡ºï¼ˆå††ï¼‰
  const amountMatch = text.match(/(\d{1,3}(?:,\d{3})*)å††|é‚„å…ƒé¡[:ï¼š]?\s*(\d{1,3}(?:,\d{3})*)/);
